#include <iostream>#include <string>using namespace std;int main(){	string nombre = "bcadeokflswxznmhi";	string aux = "";	int cont = 0;	for (int i = 1; i <= nombre.size(); ++i)	{		for (int j = 0; j < nombre.size() - 1; ++j)		{			if(nombre[j] > nombre[j + 1])			{				aux[cont] = nombre[j];				nombre[j] = nombre[j + 1];				nombre[j + 1] = aux[cont];			}		}		cont++;	}	cout << nombre << endl;	return 0;}